mapper.update:
update t_sys_msg
modify_date = #{modifyDate}, pub_date = #{pubDate}, title = #{title}, content = #{content}, cancel = #{cancel}, cancel_date = #{cancelDate}, msg_type = #{msgType}, msg_status = #{msgStatus}, user_role = #{userRole},
where id = #{id}

mapper.sql:
from
	t_sys_msg

	
	and
		title like #{title}
	
	
	and
		msg_type = #{msgType}
	
	
	and
		user_role = #{userRole}
