mapper.update:
update t_inter_param
modify_date = #{modifyDate}, code = #{code}, name = #{name}, description = #{description}, type = #{type}, format = #{format}, position = #{position}, required = #{required}, cust_schema = #{custSchema}, ext_schema = #{extSchema}, ref_schema_id = #{refSchemaId}, def_value = #{defValue},
where id = #{id}

mapper.delete:
delete from
	t_inter_param
where
	inter_id = #{interId}
and
	doc_id = #{docId}
