mapper.update:
update t_resp_schema
modify_date = #{modifyDate}, module_id = #{moduleId}, code = #{code}, name = #{name}, description = #{description}, cust_schema = #{custSchema}, type = #{type}, ref_schema_id = #{refSchemaId},
where id = #{id} and doc_id = ${docId}

mapper.delete:
delete from
	t_resp_schema
where
	id = #{schemaId}
and
	doc_id = #{docId}

mapper.sql:
from
	t_resp_schema
where
	doc_id = #{docId}

and
	module_id = #{moduleId}



and
	code like #{code}



and
	name like #{name}



and
	description like #{description}
