mapper.update:
update t_proj_mem
modify_date = #{modifyDate}, proj_id = #{projId}, user_id = #{userId}, role = #{role}, proj_role_id = #{projRoleId},
where id = #{id}

update 
	t_proj_mem
set
	role = #{role}
where
	proj_id = #{projId}
and
	user_id = #{userId}

mapper.sql:
from t_proj_mem pm inner join t_user_basic u on pm.user_id = u.id inner join t_user_detail d on u.id = d.user_id where pm.proj_id = #{projId}
and d.nick_name like #{nickName}
and u.email like #{email}
and pm.role = #{role}

mapper.delete:
delete from
	t_proj_mem
where
	user_id = #{userId}
and
	proj_id = #{projId}
