mapper.update:
update t_proj
modify_date = #{modifyDate}, user_id = #{userId}, code = #{code}, name = #{name}, description = #{description}, status = #{status},
where id = #{id}

mapper.sql:
from t_proj_mem pm inner join t_proj p on pm.proj_id = p.id where pm.user_id = #{userId}
and p.code like #{code}
and p.name like #{name}
and p.status = #{status}

from t_proj p inner join t_user_detail d on p.user_id = d.user_id left join (select proj_id,count(id) as mem_count from t_proj_mem group by proj_id) c on p.id = c.proj_id
and p.code like #{code}
and d.nick_name like #{nickName}
and p.status = #{status}
