mapper.update:
update t_user_msg
modify_date = #{modifyDate}, sys = #{sys}, sys_msg_id = #{sysMsgId}, sender_id = #{senderId}, receiver_id = #{receiverId}, pub_date = #{pubDate}, title = #{title}, content = #{content}, msg_type = #{msgType}, deal = #{deal}, deal_date = #{dealDate},
where id = #{id}

mapper.sql:
from
	t_user_msg

	
	and
		receiver_id = #{userId} 
	
	
	and
		title like #{title}
	
	
	
	and
		msg_type = #{msgType}
	
	
	
	and
		sys = #{sys}
	
	
	
	and
		deal = #{deal}

mapper.delete:
delete from
	t_user_msg
where
	id = ${msgId}
and
	receiver_id = #{userId}
